import { useState } from "react";


const App = () => {
  const [counter, setCounter] = useState(0)

  const increaseByOne = () => setCounter(counter + 1)

  const setToZero = () => setCounter(0)

  return(
    <div>
      {counter}
      <button onClick={() => setCounter(increaseByOne)}>
      plus
      </button>
      <button onClick={() => setCounter(setToZero)}>
        zero
      </button>
    </div>
  )
}


export default App;
