import { useState } from 'react'


const Statistics = (props) => {
  let positive = 0, average = 0

  if (props.data[1] == 0 && props.data[2] == 0) {
      positive = props.data[0];
  } else {
      positive = props.data[0] / (props.data[0] + props.data[1] + props.data[2])
      positive *= 100
  }

  console.log(positive)

  return(
    <div>
      <p>average {average}</p>
      <p>positive {positive} %</p>
    </div>
  )
}

const Display = (props) => {
  return(
    <h1>{props.counter}</h1>
  )
}

const Display_two = (props) => {
  return(
    <div>
      <p>{props.text} {props.counter}</p>
    </div>
  )
}

const Button = (props) => {
  return (
      <button onClick={props.handleClick}>{props.text}</button>
  )
}


const App = () => {
  const headers = ["give feedback", "statistics"]

  const [good = 0, setGood] = useState(0)
  const [neutral = 0, setNeutral] = useState(0)
  const [bad = 0, setBad] = useState(0)

  const good_add = () => setGood(good + 1)
  const neutral_add = () => setNeutral(neutral + 1)
  const bad_add = () => setBad(bad + 1)

  return (
    <div>
      <Display counter={headers[0]}/>
      <Button handleClick={good_add} text={"good"}/>
      <Button handleClick={neutral_add} text={"neutral"}/>
      <Button handleClick={bad_add} text={"bad"}/>
      <Display counter={headers[1]}/>
      <Display_two text={"good"} counter={good}/>
      <Display_two text={"neutral"} counter={neutral}/>
      <Display_two text={"bad"} counter={bad}/>
      <Statistics data={[good, neutral, bad]}/>
    </div>
  )
}

export default App;
