import { useState } from 'react'


const Display = (props) => {
  return(
    <h1>{props.counter}</h1>
  )
}

const Display_two = (props) => {
  return(
    <div>
      <p>{props.text} {props.counter}</p>
    </div>
  )
}

const Button = (props) => {
  return (
      <button onClick={props.handleClick}>{props.text}</button>
  )
}


const App = () => {
  const header_one = "give feedback"
  const header_two = "statistics"

  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const good_add = () => setGood(good + 1)
  const neutral_add = () => setNeutral(neutral + 1)
  const bad_add = () => setBad(bad + 1)

  return (
    <div>
      <Display counter={header_one}/>
      <Button handleClick={good_add} text={"good"}/>
      <Button handleClick={neutral_add} text={"neutral"}/>
      <Button handleClick={bad_add} text={"bad"}/>
      <Display counter={header_two}/>
      <Display_two text={"good"} counter={good}/>
      <Display_two text={"good"} counter={neutral}/>
      <Display_two text={"good"} counter={bad}/>
    </div>
  )
}

export default App;
