import {useState, useEffect} from 'react'
import axios from 'axios'

const Filter = ({filter, setFilter}) => {
  const FilterFunc = (event) => {
    setFilter(event.target.value)
  }  
  return ( 
      <form>
        <div>
          filter show with<input value={filter} onChange={FilterFunc}/>
        </div>
      </form>
  )
}

const PersonForm = ({data, setData}) => {
  const [name, setName] = useState('')
  const [number, setNumber] = useState('')
  const NameField = (event) => {
    event.preventDefault()
    setName(event.target.value)  
  }
  const NumberField = (event) => {
    event.preventDefault()
    setNumber(event.target.value) 
  }
  const addNumber = (event) => {
    event.preventDefault()
    const personObject = {
      name: name,
      number: number,
      id: data.length + 1,
    }
    setData(data.concat(personObject))
  }
  //console.log(name + "  " + number);
  return (
    <form>
      <div>name:<input value={name} onChange={NameField}/></div>
      <div>number:<input value={number} onChange={NumberField}/></div>
      <button onClick={addNumber}>add</button>
    </form>
  )
}

const Persons = () => {

}

const App = () => {
  // Get data from json server
  const URL = 'http://localhost:3001/persons'
  const [data, setData] = useState([])
  const [filter, setFilter] = useState('')
  useEffect(() => {
  axios
    .get(URL)
    .then(response => {
      setData(response.data)
    })
  }, [])

  console.log(filter)
  console.log(data)

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter
      filter={filter} 
      setFilter={setFilter}
      />

      <h3>Add a new</h3>
      <PersonForm 
      data={data}
      setData={setData}
      />

      <h3>Numbers</h3>

      <Persons/>
    </div>
  )
}

export default App
