import { useState, useEffect } from 'react'
import Grud from './services/hooks'

// Done
const Filter = ({ filter, setFilter }) => {
  return (
    <form>
      filter shown with <input value={filter} onChange={event => setFilter(event.target.value)} />
    </form>
  )
}

const PersonForm = ({ data }) => {
  const [username, setUsername] = useState('')
  const [number, setNumber] = useState('')

  const handleAdd = (event) => {
    if (username != '' && number != '') {
      event.preventDefault()
      const newObject = {
        name: username,
        number: number,
        id: (data.length + 1),
      }
      Grud.create(newObject)
    }
  }

  return (
    <form>
      name: <input value={username} onChange={event => setUsername(event.target.value)} />
      <br />
      number: <input value={number} onChange={event => setNumber(event.target.value)} />
      <br />
      <button onClick={(event) => handleAdd(event)}>add</button>
    </form>
  )
}

const Persons = ({ data, updater }) => {

  const remove = (event, person) => {
    event.preventDefault()
    Grud.remove(person)

  }

  return (
    <div>
      {data.map(person =>
          <p key={person.id}>{person.name} {person.number} 
          <button onClick={(event) => remove(event, person.id)}>delete</button>
          </p>
      )}
    </div>
  )
}

const App = () => {
  const [filter, setFilter] = useState('')
  const [persons, setPersons] = useState([])
  const [update, setUpdate] = useState(0);

  useEffect(() => {
    Grud.getAll()
      .then(res => setPersons(res.data))
  }, [update])

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter
        filter={filter}
        setFilter={setFilter}
      />
      <h3>Add a new</h3>
      <PersonForm 
        data={persons}
        update={update}
        setUpdate={setUpdate}
      />
      <h3>Numbers</h3>
      <Persons
        data={persons}
        update={update}
        setUpdate={setUpdate}
      />
    </div>
  )
}

export default App
