import { useState, useEffect } from 'react'
import Grud from './services/hooks'

// Done
const Filter = ({ filter, setFilter }) => {
  return (
    <form>
      filter shown with <input value={filter} onChange={event => setFilter(event.target.value)} />
    </form>
  )
}

const PersonForm = ({data, refresh, setRefresh}) => {
  const [username, setUsername] = useState('')
  const [number, setNumber] = useState('')



  const handleAdd = (event) => {
    event.preventDefault()
    if (username != '' && number != '') {
      const newObject = {
        name: username,
        number: number,
        id: (data.length - 1),
      }
      //Grud.create(newObject)
      console.log(newObject);
      setRefresh(refresh + 1)
    }
  }

  return (
    <form>
      name: <input value={username} onChange={event => setUsername(event.target.value)} />
      <br />
      number: <input value={number} onChange={event => setNumber(event.target.value)} />
      <br />
      <button onClick={handleAdd}>add</button>
    </form>
  )
}


const Persons = ({ data, refresh, setRefresh }) => {

  const remove = (event, person) => {
    event.preventDefault()
    Grud.remove(person)
    setRefresh(refresh + 1)
  }

  return (
    <div>
      {data.map(person =>
          <p key={person.id}>{person.name} {person.number} 
          <button onClick={(event) => remove(event, person.id)}>delete</button>
          </p>
      )}
    </div>
  )
}

const App = () => {
  const [filter, setFilter] = useState('')
  const [persons, setPersons] = useState([])
  const [refresh, setRefresh] = useState(0)

  useEffect(() => {
    Grud.getAll()
      .then(res => setPersons(res.data))
  }, [refresh])

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter
        filter={filter}
        setFilter={setFilter}
      />
      <h3>Add a new</h3>
      <PersonForm 
        data={persons}
        refresh={refresh}
        setRefresh={setRefresh}
      />
      <h3>Numbers</h3>
      <Persons
        data={persons}
        refresh={refresh}
        setRefresh={setRefresh}
      />
    </div>
  )
}

export default App
